SELECT TO_CHAR (force_matching_signature) force_matching_signature,cnt,to_char(last_load,'YYYY-MM-DD/HH24:MI:SS') as first_load,to_char(last_load,'YYYY-MM-DD/HH24:MI:SS') as last_load,(SELECT substr(sql_text,1, 60) FROM v$sql sa WHERE     sa.force_matching_signature = dup.force_matching_signature  AND ROWNUM = 1) sql_text  FROM (  SELECT force_matching_signature,COUNT (1) cnt,  MIN (last_active_time) first_load,MAX (last_active_time) last_load FROM v$sql WHERE force_matching_signature > 0 AND force_matching_signature <> exact_matching_signature GROUP BY force_matching_signature HAVING COUNT (1) > 10 ORDER BY 2 DESC) dup WHERE ROWNUM < 26