SELECT tablespace_name  as TBSName,count(*)  as NumObjects,sum(round(used_space/1024/1024,2)) as  UsedMb, sum(round(allocated_space/1024/1024,2)) as AllocMb,sum(round(reclaimable_space/1024/1024,2)) as ReclaimMb,round((sum(reclaimable_space)/sum(allocated_space))*100) as PctSaved    FROM TABLE (DBMS_SPACE.asa_recommendations ('FALSE', 'FALSE', 'FALSE'))GROUP BY tablespace_name ORDER BY sum(round(reclaimable_space/1024/1024,2)) desc