SELECT * from (SELECT 'Table' as ObjectType,owner as Schema1,table_name as Name,NULL  as PartitionName FROM sys.dba_tables WHERE last_analyzed IS NULL AND owner NOT IN ('SYS', 'SYSTEM','CTXSYS','ODM','ORDSYS','WKSYS','XDB','APEX_030200', 'DBSNMP','ORDPLUGINS','PERFSTAT','OUTLN','EXFSYS','MDSYS','OLAPSYS','ORDDATA', 'SQLTXPLAIN','SYSMAN','WMSYS','MFXDBA')AND partitioned = 'NO' UNION SELECT 'Index' as ObjectType, owner as Schema1,index_name as Name, NULL as PartitionName FROM sys.dba_indexes WHERE last_analyzed IS NULL AND owner NOT IN('SYS','SYSTEM','CTXSYS','ODM','ORDSYS','WKSYS','XDB','APEX_030200','DBSNMP','ORDPLUGINS','PERFSTAT','OUTLN','EXFSYS','MDSYS','OLAPSYS','ORDDATA','SQLTXPLAIN','SYSMAN','WMSYS','MFXDBA')AND partitioned = 'NO' AND index_type != 'LOB' AND temporary != 'Y' UNION SELECT 'Table Partition'  as ObjectType,table_owner as Schema1,table_name as Name, partition_name as PartitionName FROM sys.dba_tab_partitions 
WHERE last_analyzed IS NULL AND table_owner NOT IN('SYS', 'SYSTEM','CTXSYS','ODM','ORDSYS','WKSYS','XDB','APEX_030200', 'DBSNMP','ORDPLUGINS','PERFSTAT','OUTLN','EXFSYS','MDSYS','OLAPSYS','ORDDATA', 'SQLTXPLAIN','SYSMAN','WMSYS','MFXDBA') UNION SELECT 'Index Partition' as ObjectType, index_owner as Schema1,index_name as Name,partition_name as PartitionName FROM sys.dba_ind_partitions  WHERE last_analyzed IS NULL AND index_owner NOT IN ('SYS', 'SYSTEM','CTXSYS','ODM','ORDSYS','WKSYS','XDB','APEX_030200','DBSNMP','ORDPLUGINS','PERFSTAT','OUTLN','EXFSYS','MDSYS','OLAPSYS','ORDDATA', 'SQLTXPLAIN','SYSMAN','WMSYS','MFXDBA')) a WHERE NOT EXISTS(SELECT 1 FROM dba_recyclebin WHERE owner = a.Schema1 AND a.Name = object_name) ORDER BY Schema1, ObjectType, Name
                