SELECT * FROM (  SELECT sql_id as SQL_ID,last_load_time as Last_Load, to_char(last_active_time,'YYYY-MM-DD/HH24:MI:SS')  as Last_Active,sorts as Sorts,loaded_versions as Load_Version,open_versions as Open_Version,users_opening as User_Opening,fetches as Fetches,executions as Execs,disk_reads as Disk_Read,buffer_gets as Buffer_Gets,elapsed_time as Elapsed_Time FROM v$sql ORDER BY elapsed_time DESC) a WHERE ROWNUM < 11