SELECT 'CASCADE' as Parameter, DBMS_STATS.get_param ('CASCADE') as Value,DECODE (DBMS_STATS.get_param ('CASCADE'), 'DBMS_STATS.AUTO_CASCADE', 'TRUE', 'FALSE')  as Default1  FROM DUAL UNION SELECT 'DEGREE',  DBMS_STATS.get_param ('DEGREE'), DECODE (DBMS_STATS.get_param ('DEGREE'), 'NULL', 'TRUE', 'FALSE')  as Default1  FROM DUAL UNION SELECT 'ESTIMATE PERCENT',DBMS_STATS.get_param ('ESTIMATE_PERCENT'),  DECODE (DBMS_STATS.get_param ('ESTIMATE_PERCENT'), 'DBMS_STATS.AUTO_SAMPLE_SIZE', 'TRUE',  'FALSE') as Default1 FROM DUAL UNION SELECT 'METHOD OPT', DBMS_STATS.get_param ('METHOD_OPT'), DECODE (DBMS_STATS.get_param ('METHOD_OPT'), 'FOR ALL COLUMNS SIZE AUTO', 'TRUE',  'FALSE') as Default1  FROM DUAL UNION  SELECT 'NO INVALIDATE',       DBMS_STATS.get_param ('NO_INVALIDATE'), DECODE (DBMS_STATS.get_param ('NO_INVALIDATE'),   'DBMS_STATS.AUTO_INVALIDATE', 'TRUE',  'FALSE') as Default1  FROM DUAL UNION SELECT 'GRANULARITY', DBMS_STATS.get_param ('GRANULARITY'),   DECODE (DBMS_STATS.get_param ('GRANULARITY'), 'AUTO', 'TRUE', 'FALSE')  as Default1  FROM DUAL UNION SELECT 'AUTOSTATS_TARGET', DBMS_STATS.get_param ('AUTOSTATS_TARGET'), DECODE (DBMS_STATS.get_param ('AUTOSTATS_TARGET'),  'AUTO', 'TRUE',  'FALSE') as Default1 FROM DUAL ORDER  BY 1