SELECT client_name  as Job,decode (status, 'ENABLED', 'TRUE', 'FALSE') as Enabled1,job_start_time as Last_Run,to_char(job_duration) as Duration,trunc(sysdate) - trunc(job_start_time) as Days_since_last_run FROM ( SELECT ac.client_name,ac.status,ah.job_start_time,ah.job_duration FROM dba_autotask_client ac, DBA_AUTOTASK_JOB_HISTORY ah WHERE ac.client_name = 'auto optimizer stats collection' AND ah.client_name (+) =  ac.client_name ORDER BY ah.job_start_time desc ) where rownum <=1