SELECT a.client_name as Job,decode (a.status, 'ENABLED', 'TRUE', 'FALSE') as Enabled1,c.window_name as Window1,c.schedule_name as Schedule_Name,c.start_date as Start_Date,c.repeat_interval as Repeat_Interval,to_char(c.duration) as Duration FROM dba_autotask_client a,dba_scheduler_wingroup_members b,dba_scheduler_windows c WHERE a.client_name = 'auto optimizer stats collection' AND a.window_group=b.window_group_name AND b.window_name = c.window_name